// 正确的解法: 应该用栈来解决.
// ...

// 这个解法(循环删除合法的括号)可以得到正确的结果,
//但是当 TestCase 足够长的时候, 会提示超时.
bool isValid(char * s) {
    
    size_t prevLength = 0;
    for (int i = 0; strlen(s) > 0; ++i) {
        size_t length = strlen(s);
        if (i >= length - 1) {
            i = 0;
            if (length == prevLength) {
                return false;
            }
            prevLength = length;
        }
        if ((s[i] == '(' && s[i + 1] == ')') ||
            (s[i] == '[' && s[i + 1] == ']') ||
            (s[i] == '{' && s[i + 1] == '}')) {
            memcpy(&s[i], &s[i+2], length - i - 1);
//            printf("处理后的: %s\n", s);
        }
    }
    
    return true;
}
